import React from 'react';

const productAdd = function () {

    const PizzaProducts = require('./json/products.json');

    const productArr = Object.values(PizzaProducts.pizzas);

    let product;

    productArr.map(function (value, index) {
        const li = document.createElement('li');
        li.classList.add('product');

        const divImg = document.createElement('div');
        const divDes = document.createElement('div');
        const divBtn = document.createElement('div');

        divImg.classList.add('product-img');
        divDes.classList.add('product-description');
        divBtn.classList.add('add-container');

        const img = document.createElement('img');
        img.setAttribute('src', `${value.img}`);
        divImg.appendChild(img);

        const h3 = document.createElement('h3');
        const size = document.createElement('div');
        const des = document.createElement('div');

        size.classList.add('size-weight');
        des.classList.add('description');

        h3.innerHTML = `${value.name}`;
        size.innerHTML = `${value.size}`;
        des.innerHTML = `${value.description}`;

        divDes.appendChild(des);
        divDes.insertBefore(size, des);
        divDes.insertBefore(h3, size);

        const cost = document.createElement('div');
        const btn = document.createElement('button');

        cost.classList.add('cost');
        btn.classList.add('add-btn');

        cost.innerHTML = `${value.cost}`;
        btn.innerHTML = 'В корзину';

        divBtn.appendChild(btn);
        divBtn.insertBefore(cost, btn);

        li.appendChild(divBtn);
        li.insertBefore(divDes, divBtn);
        li.insertBefore(divImg, divDes);

        product = li;
    });

    return(
            <div className="li-product-container-wrapper"></div>
    )
};

export default productAdd;
