import React from 'react';

const Slider = function () {

    let position = 0;
    const slidesToShow = 1;
    const slidesToScroll = 1;
    const items = document.querySelectorAll('slider-item');
    const itemsCount = items.length;
    const itemWidth = 100;
    const movePosition = slidesToScroll * itemWidth;

    items.forEach((item) => {
        item.style.minWidth = `${itemWidth}%`;
    });

    const btnNext = function () {
        const itemsLeft = itemsCount - (Math.abs(position) + slidesToShow * itemWidth) / itemWidth;

        position -= itemsLeft >= slidesToScroll ? movePosition : itemsLeft * itemWidth;

        setPosition();
        checkBtns();
    };

    const btnPrev = function () {
        const itemsLeft = Math.abs(position) / itemWidth;

        position -= itemsLeft >= slidesToScroll ? movePosition : itemsLeft * itemWidth;

        setPosition();
        checkBtns();
    };

    const setPosition = function () {
        const slider = document.querySelector('.slider');
        slider.style.transform = `translateX(${position}px)`;
    };
    const checkBtns = function () {
        btnPrev.disabled = position === 0;
        btnNext.disabled = position <= -(itemsCount - slidesToShow) * itemWidth;
    };

    return (
        <div className="slider-container">
            <ul className="slider">
                <li className="slider-item"></li>
                <li className="slider-item"></li>
            </ul>

            <button id="slider1_next" className="button_next" onClick={btnNext}>&gt;</button>
            <button id="slider1_prev" className="button_prev" onClick={btnPrev}>&lt;</button>

        </div>
    );
};

export default Slider;
