import React, { useContext, useState } from 'react';
import Context from './../context';

const PizzaProducts = function () {

    // let productsCard = {
    //     "pizzas" : {

    //     },

    //     "snacks": {

    //     },

    //     "snacksset": {

    //     },

    //     "snacksfreetur": {

    //     },

    //     "snacksrolls": {

    //     },

    //     "desserts": {

    //     },

    //     "drinks": {

    //     },

    //     "sauces": {

    //     }
    // };

    const PizzaProducts = require('./../json/products.json');

    const productArr = Object.values(PizzaProducts.pizzas);

    let {cart, setCart} = useContext(Context);

    // const pizzasCart = useContext(Context)

    // const [pizzasCart, setPizzasCart] = useState(function() {
    //     let bin = JSON.parse(localStorage.getItem("pizzas")) || [];
    //     return bin;
    // });

    // const [cart, setCart] = useState(function(){
    //     let cart = cartContext.cart;
    //     return cart;
    // });

    const toCart = function (event) {
        const li = event.target.closest('li');
        let cartArr = cart.split(',');
        cartArr.push(li.dataset.id);
        if(cartArr[0] === "") cartArr.shift()
        let cartStr = cartArr.join(',');
        setCart(cartStr)

        localStorage.setItem('pizzas', cartStr);
        getElem(event);
    };

    const getElem = function (event) {
        const li = event.target.closest('li');
        const img = li.querySelector('img').src.substring(21);
        const titleValue = li.querySelector('h3').innerHTML;
        const cost = li.querySelector('.cost').innerHTML;
    };

    return (
        <ul className="pizzas-goods">{
            productArr.map((item, index) =>
                <li className="product" key={index} data-id={index}>
                    <div className="product-img">
                        <img src={item.img}></img>
                    </div>
                    <div className="product-description">
                        <h3>{item.name}</h3>
                        <div className="size-weight">{item.size}</div>
                        <div className="description">{item.description}</div>
                    </div>
                    <div className="add-container">
                        <div className="cost">{item.cost}</div>
                        <button className="add-btn" onClick={toCart} data-id={item.id}>В корзину</button>
                    </div>
                </li>
            )    
        }</ul>
    );
};

export default PizzaProducts;
