import React from 'react';

const Slider = function () {

    const images = require('./json/images.json');

    const slidesArr = Object.values(images.slides);

    let marginLeft = 100;

    const nextSlide = function () {
        const slides = document.querySelectorAll('.slider-container ul li');
        slides[0].setAttribute('style', `margin-left: -${marginLeft}%`)
        marginLeft = marginLeft + 100;
    };

    // const prevNext = function () {
