import React, { useState, useContext, useEffect } from 'react';
import Context from './context';

import productsData from './json/products.json';

const CartAdd = function() {

    let {cart, setCart} = useContext(Context);

    let cartProduct = [];
    let cartProductJsx = <div className='cart-item'><div className="cart-empty-text">Корзина пуста</div></div>;

    const deleteItem = function (event) {
        const div = event.target.closest('.cart-item');
        // console.log(div.dataset.id, div.dataset.type)
        let cartObj = JSON.parse(cart);
        console.log(cartObj[div.dataset.type])
        console.log(div.dataset.type, div.dataset.index)
        cartObj[div.dataset.type].splice(div.dataset.index, 1)
        let cartStr = JSON.stringify(cartObj);
        setCart(cartStr);
        localStorage.setItem('cart', cartStr)
        console.log(cart)
    };

    if (cart.length === 0) {
        return cartProductJsx;
    } else {
        let cartData = JSON.parse(cart);

        let keys = Object.keys(cartData)



        keys.forEach(key => {
            cartData[key].forEach(indexProduct => {
                productsData[key]['product' + (+indexProduct + 1)].type = key;
                productsData[key]['product' + (+indexProduct + 1)].id = indexProduct;
                cartProduct.push(productsData[key]['product' + (+indexProduct + 1)])
            });
        });
        if (cartProduct.length !== 0) {
            let cartU = [];
            cartProductJsx = cartProduct.map((item, index) => {
                let cartObj = JSON.parse(cart);
                console.log(cartObj[item.type])
                console.log(item.id)

                let amount = 0;

                cartObj[item.type].forEach(cartItem => {
                    if (cartItem === item.id) amount++;
                })

                if(amount === 0) amount = 1;

                if(amount > 1 && cartU.indexOf(item.id) === -1) cartU.push(index); 

                return  <div className="cart-item" key={index} data-index={index} data-type={item.type}>
                            <div className="item-image"><div className='image-wrapper'><img src={item.img}></img></div></div>
                            <div className="item-title">{item.name}</div>
                            <div className="item-amount">
                                <div className="amount-changer"><span>-</span></div>
                                <div className="amount">{amount}</div>
                                <div className="amount-changer"><span>+</span></div>
                            </div>
                            <div className="item-price"><span className="price-value">{item.cost}</span></div>
                            <div className="item-delete"><div className="delete-btn" onClick={deleteItem}><div className="cross"></div></div></div>
                        </div>
            })
            console.log(cartU)
            if (cartU.length > 0) cartProductJsx.filter((item, index) => {
                console.log(item)
                if (cartU.indexOf(index) !== -1) return item;
            })
        }
        return cartProductJsx;
    }
};

export default CartAdd;
