import React from 'react';

const Slider = function () {

    let marginLeft = '0';

    const nextSlide = function () {
        const slider = document.querySelector('.slider');

        const slide1 = document.querySelector('.slide1');
        const slide2 = document.querySelector('.slide2');
        const lastSlide = document.querySelector('.slide3');

        slide1.style.marginLeft = '-100%'

    };

    const prevSlide = function () {
        const slider = document.querySelector('.slider');

        const slide1 = document.querySelector('.slide1');
        const slide2 = document.querySelector('.slide2');
        const lastSlide = document.querySelector('.slide3');
    };

    return (
        <div className="slider-container">
            <ul className="slider">
                <li className="slide1"></li>
                <li className="slide2"></li>
                <li className="slide3"></li>
            </ul>

            <button id="slider1_next" className="button_next" onClick={nextSlide}>&gt;</button>
            <button id="slider1_prev" className="button_prev" onClick={prevSlide}>&lt;</button>

        </div>
    );
};

export default Slider;
