import React from 'react';

const Amount = function () {
    let amount = 1;

    const plusItem = function () {
        amount = amount + 1;
    };

    const minusItem = function () {
        amount = amount - 1;
    }

    return <div className="item-amount">
                <div className="amount-changer" onClick={minusItem}><span>-</span></div>
                <div className="amount">{amount}</div>
                <div className="amount-changer" onClick={plusItem}><span>+</span></div>
            </div>
};

export default Amount;
