import React from 'react';

const Slider = function () {

    const images = require('./json/images.json');

    const slidesArr = Object.values(images.slides);

    let slider;

    let buttonNext;
    let buttonPrev;

    const nextPrev = function(action){
        slider = document.querySelector(`.slider`);
        buttonNext = document.querySelector('.button_next');
        buttonPrev = document.querySelector('.button_prev');

        const slides = slider.querySelectorAll('li');

        let slide1 = slides[0];
        const slideLast = slides[slides.length-1];

        buttonNext.disabled = true;
        buttonPrev.disabled = true;
        let slide1Clone;
        let slideLastClone;

        let marginLeft = Math.abs(parseInt(slide1.style.marginLeft));
        if (!marginLeft) marginLeft = 0;
        if (action == 'next' && marginLeft == (slides.length*100)-100) {
            slide1Clone = slide1.cloneNode(true);
            slide1Clone.classList.add('clone');
            slide1Clone.style.marginLeft = '0';
            slider.appendChild(slide1Clone);
            if (!slide1Clone) marginLeft=-100;
        }
        if (action == 'prev' && marginLeft == 0 ) {
            marginLeft = (slides.length*100);

           slideLastClone = slideLast.cloneNode(true);
           slideLastClone.classList.add('clone');
           slider.insertBefore(slideLastClone, slide1);
           slide1.style.marginLeft = '';
           slide1 = slideLastClone;
           slide1.style.marginLeft = '-100%';
           marginLeft = 100;
        }

        const slideStep = 100;
        let slideStartMl = marginLeft;

        const slide = function() {
            slider = document.querySelector(`.slider`);
            buttonNext = document.querySelector('.button_next');
            buttonPrev = document.querySelector('.button_prev');

            const slides = slider.querySelectorAll('li');

            let slide1 = slides[0];
            const slideLast = slides[slides.length-1];
            marginLeft = marginLeft + 2 * (action == 'prev' ? -1 : 1);
            slide1.style.marginLeft = `-${marginLeft}%`;
            if ((action == 'next' && marginLeft < (slideStartMl + slideStep)) ||
                (action == 'prev' && marginLeft > (slideStartMl - slideStep))){
                window.requestAnimationFrame(slide);
            } else {
                buttonNext.disabled = false;
                buttonPrev.disabled = false;
                if (slide1Clone) {
                    slide1Clone.remove();
                    slide1Clone = undefined;
                    slide1.style.marginLeft = 0;
                }
                if (slideLastClone) {
                    slideLastClone.remove();
                    slideLastClone = undefined;
                    marginLeft = (slides.length*100)-100;
                    slide1 = slides[0];
                    slide1.style.marginLeft = `-${marginLeft}%`;
                }
            }
        };
        slide();
    };

    return (
        <div className="slider-container">
            <ul className="slider">
                {slidesArr.map((item, index) => {
                    return <li data-index={index} key={index}><img src={item}/></li>
                })}
            </ul>

            <button id="slide1_next" className="button_next" onClick={nextPrev}>&gt;</button>
            <button id="slide1_prev" className="button_prev" onClick={nextPrev}>&lt;</button>

        </div>
    );
};

export default Slider;
