import React, {useContext, useState, useEffect} from 'react';
import {NavLink} from 'react-router-dom';
import Context from './context';
import CartAdd from './CartAdd';

function Cart() {

    let {cart, setCart, totalCost, setTotalCost} = useContext(Context);

    const openPopup = function () {
        const popup = document.querySelector('.order-popup');
        popup.classList.add('popup-open');
    };

    const exitPopup = function () {
        const popup = document.querySelector('.order-popup');
        popup.classList.remove('popup-open');
    };

    // const checkTotalCost = function (){
	// 	let cost = 0;

	// 	if(cart === '') return;

	// 	const cartObj = JSON.parse(cart);
    //     console.log(cartObj)

	// 	let keys = Object.keys(cartObj);

    //     keys.forEach(key => {
	// 		// cost += cartObj[key].length;
    //         // console.log(key)
    //         console.log(cartObj[key])
	// 		return cost;
	// 	});

	// 	setTotalCost(cost);
	// };

	// useEffect(function () {
	// 	checkTotalCost();
	// }, [cart]);

    return (
        <div className="сart">
            <div className="cart-container">
                <a name="top"></a>
                <h3 className="cart-title">Корзина</h3>
                <div className="cart-list">
                    <CartAdd />
                </div>
                <div className="cart-alert">Внимание, минимальная сумма заказа для доставки 15 руб.<br></br>Чтобы добавить позицию в заказ <NavLink href="#top" to="/">вернитесь в меню</NavLink></div>
                <div className="cart-cost">
                    <div className="cart-cost-container">
                        Сумма заказа:<span><span>{totalCost}</span>руб.</span>
                    </div>
                </div>
                <div className="cart-btn-container">
                    <button className="return-btn"><NavLink to='/'>Вернуться в меню</NavLink></button>
                    <button className="order-btn" onClick={openPopup}><a href="#">Заказать</a></button>
                </div>
                <div className="order-popup"> {/* popup-open класс */}
                    <div className="popup-container">
                        <h3>Куда доставить?</h3>
                        <div className="form-container">
                            <div className="input-wrapper lg-2">
                                <input className="form-input" type="text" name="Имя" placeholder="Ваше имя" required></input>
                            </div>
                            <div className="input-wrapper lg-2">
                                <input className="form-input" type="tel" name="Номер телефона" placeholder="Контактный номер телефона" required></input>
                            </div>
                            <div className="input-wrapper lg-3">
                                <input className="form-input" type="text" name="Улица" placeholder="Улица" required></input>
                            </div>
                            <div className="input-wrapper lg-1">
                                <input className="form-input" type="text" name="Дом" placeholder="Дом" required></input>
                            </div>
                            <div className="input-wrapper lg-1">
                                <input className="form-input" type="number" name="Квартира" placeholder="Квартира"></input>
                            </div>
                            <div className="input-wrapper lg-1">
                                <input className="form-input" type="text" name="Подъезд" placeholder="Подъезд"></input>
                            </div>
                            <div className="input-wrapper lg-1">
                                <input className="form-input" type="text" name="Код двери" placeholder="Код двери"></input>
                            </div>
                            <div className="input-wrapper lg-1">
                                <input className="form-input" type="number" name="Этаж" placeholder="Этаж"></input>
                            </div>
                            <div className="input-wrapper lg-4">
                                <textarea className="form-textarea" name="Комментарий" maxLength="254" rows="3" placeholder="Комментарий"></textarea>
                            </div>
                            <div className="payment-wrapper">
                                <hr></hr>
                                <h5>Оплата</h5>
                                <div className="payment">
                                    <div className="payment-child">
                                        <input className="payment-input" type="radio" name="Оплата" value="Наличными"></input>
                                        <label className="label-control">Наличными</label>
                                    </div>
                                    <div className="payment-child">
                                        <input className="payment-input" type="radio" name="Оплата" value="Картой курьеру"></input>
                                        <label className="label-control">Картой курьеру</label>
                                    </div>
                                    <div className="payment-child">
                                        <input className="payment-input" type="radio" name="Оплата" value="Картой онлайн"></input>
                                        <label className="label-control">Картой онлайн</label>
                                    </div>
                                </div>
                            </div>
                            <button className="confirm-btn" type="submit">Подтвердить заказ</button>
                        </div>
                        <button className="form-close-btn" onClick={exitPopup}>
                            <i></i>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    )
};

export default Cart;
