import React from 'react';

const Slider = function () {

    const nextSlide = function () {
        const slider = document.querySelector('.slider');
        const sliderArray = Object.values(slider.children);

        const slide1 = sliderArray[0];
        const slideLast = sliderArray[sliderArray.length - 1];

        let slide1Clone;

        if (slider.querySelector('.slide1clone') === null) {
            slide1Clone = slide1.cloneNode(true);
            slide1Clone.classList.add('slide1clone');
            slider.appendChild(slide1Clone);

            slide1.style.marginLeft = '0%';
        }

        if (slide1.style.marginLeft === '0%') {
            slide1.style.marginLeft = '-100%';
            slide1.style.transition = ''
        } else if (slide1.style.marginLeft === '-100%') {
            slide1.style.marginLeft = '-200%';
            if (slide1.style.marginLeft = '-200%') {
                slide1.style.marginLeft = '0%';
                slide1.style.transition = '0s';
                const slide1Clone = slider.querySelector('.slide1clone');
                slide1Clone.remove();
            }
        } else if (slide1.style.marginLeft = '-200%') {
            if (slide1.style.marginLeft = '-200%') {
                slide1.style.marginLeft = '0%';
                const slide1Clone = slider.querySelector('.slide1clone');
                slide1Clone.remove();
            }
        }
    };

    const prevSlide = function () {
        const slider = document.querySelector('.slider');
        const sliderArray = Object.values(slider.children);

        const slide1 = sliderArray[0];
        const slideLast = sliderArray[sliderArray.length - 1];

        let slideLastClone;

        if (slider.querySelector('.slidelastclone') === null) {
            slideLastClone = slideLast.cloneNode(true);
            slideLastClone.classList.add('slidelastclone');
            slider.insertBefore(slideLastClone, slide1);

            slide1.style.marginLeft = '0%';
        }

        if (slide1.style.marginLeft === '100%') {
            slide1.style.marginLeft = '100%';
            slide1.style.transition = ''
        } else if (slide1.style.marginLeft === '100%') {
            slide1.style.marginLeft = '200%';
            if (slide1.style.marginLeft = '200%') {
                slide1.style.marginLeft = '0%';
                const slideLastClone = slider.querySelector('.slidelastclone');
                slideLastClone.remove();
            }
        } else if (slide1.style.marginLeft = '200%') {
            if (slide1.style.marginLeft = '200%') {
                slide1.style.marginLeft = '0%';
                const slideLastClone = slider.querySelector('.slidelastclone');
                slideLastClone.remove();
            }
        }
    };

    return (
        <div className="slider-container">
            <ul className="slider">
                <li className="slide1"></li>
                <li className="slide2"></li>
            </ul>

            <button id="slider1_next" className="button_next" onClick={nextSlide}>&gt;</button>
            <button id="slider1_prev" className="button_prev" onClick={prevSlide}>&lt;</button>

        </div>
    );
};

export default Slider;
