import './media.css';
import './Cart.css';
import productImage from './images/sauce/barbekuSouce.jpg'; // но картинка будет браться из локал сторейдж

function Cart() {
    return (
        <div className="сart">
            <div className="container">
                <h3 className="cart-title">Корзина</h3>
                <div className="cart-list">
                    <div className="row">
                        {/* <div className="cart-empty-text"><h3>Корзина пуста</h3></div> */}
                        <div className="item-image"><img src={productImage}></img></div>
                        <div className="item-title">Barbeku соус</div>
                    </div>
                </div>
                <div className="cart-alert">Внимание, минимальная сумма заказа для доставки 15 руб.<br></br>Чтобы добавить позицию в заказ <a href="#">вернитесь в меню</a></div>
                <div className="cart-cost">
                    <div className="cart-cost-container">
                        Сумма заказа: <span><span id="totalPrice">0</span> руб.</span>
                    </div>
                </div>
                <div className="cart-btn-container">
                    <button className="return-btn"><a href="#">Вернуться в меню</a></button>
                    <button className="order-btn-alert"><a href="#">Заказать</a></button>
                </div>
            </div>
        </div>
    )
};

export default Cart;
