import React, {useState} from 'react';
import {BrowserRouter, Route, NavLink} from 'react-router-dom';

import Context from './context';

import logo from './phoenix-logo.png';

import inst from './images/contacts/instagram.png';
import vkon from './images/contacts/vkontakte.png';
import visa from './images/pay/visa.png';
import verVisa from './images/pay/verVisa.png';
import masterCard from './images/pay/masterCard.png';
import masterCardSecur from './images/pay/masterCardSecur.png';
import maestro from './images/pay/maestro.png';
import belcard from './images/pay/belcard.png';
import bePaid from './images/pay/bePaid.png';
import cartaPokupok from './images/pay/cartaPokupok.png';
import halva from './images/pay/halva.png';
import fun from './images/pay/fun.jpg';
import magnit from './images/pay/magnit.png';

import Catalog from './Catalog';
import Cart from './Cart';

import './media.css';
import './App.css';
import './Cart.css';

const App = function () {

	const [pizzasCart, setPizzasCart] = useState(function() {
        let bin = JSON.parse(localStorage.getItem("pizzas")) || [];
        return bin;
    });

	const hash = window.location.host;

	const openBurger = function () {
		const nav = document.querySelector('.nav-goods');
		nav.classList.add('burger-open');
		nav.classList.remove('nav-goods');

		const bg = document.querySelector('.bg');
		bg.classList.add('bg-open');
	};

	const exitBurger = function () {
		if (document.querySelector('.burger-open') === null) return;
		const nav = document.querySelector('.burger-open');
		nav.classList.add('nav-goods');
		nav.classList.remove('burger-open');

		const bg = document.querySelector('.bg');
		bg.classList.remove('bg-open');
	};

	const scroll = function () {
		window.scrollTo(0, 0);
	};

	const toPizza = function () {
		document.location.replace(`/#pizzas`);
	};
	const toSnacks = function () {
		document.location.replace(`/#snacks`);
	};
	const toDesserts = function () {
		document.location.replace(`/#desserts`);
	};
	const toDrinks = function () {
		document.location.replace(`/#drinks`);
	};
	const toSauces = function () {
		document.location.replace(`/#sauces`);
	};

	return (
		<BrowserRouter>
		<Context.Provider value={{pizzasCart, setPizzasCart}}>
			<div className="site">
				<header className="header">
					<div className="header-container">
						<div className="header-wrapper">

							<div className="header-top-left">Доставка пиццы<div>бесплатно</div></div>

							<div className="header-top-center"><a href="#"><img className='logo' src={logo}></img></a>Пицца Феникс</div>

							<div className="header-top-right">
								<div className="header-contacts">377-60-20</div>
								<div className="header-deliverytime">доставка пиццы<div>круглосуточно</div></div>
							</div>

						</div>
					</div>
				</header>

				<nav className="nav">
					<div className="nav-container">
						<div className="nav-wrapper">

							<ul className="nav-goods"> {/* burger open класс */}
								<li className="pizza-ico" onClick={exitBurger, toPizza}><a href="#pizzas"><p>Пиццы</p></a></li>
								<li className="snack-ico" onClick={exitBurger, toSnacks}><a href="#snacks"><p>Закуски</p></a></li>
								<li className="dessert-ico" onClick={exitBurger, toDesserts}><a href="#desserts"><p>Десерты</p></a></li>
