import React from 'react';

const Slider = function () {

    const slide = function (action) {
        const slider = document.querySelector('.slider');

        const slide1 = document.querySelector('.slide1');
        const slide2 = document.querySelector('.slide2');
        const lastSlide = document.querySelector('.slide3');

        let slide1Clone = slide1.cloneNode(true);
        slide1Clone.classList.add('slide1-clone');

        let slideLastClone = slide1.cloneNode(true);
        slideLastClone.classList.add('slide-last-clone');

        slider.insertBefore(slideLastClone, slide1);
        slider.appendChild(slide1Clone);

        if (action === 'next') {
            nextSlide();
        } else {
            prevSlide();
        }

        const nextSlide = function () {
            console.log('next');
        };

        const prevSlide = function () {
            console.log('prev');
        };
    };
    return (
        <div className="slider-container">
            <ul className="slider">
                <li className="slide1"></li>
                <li className="slide2"></li>
                <li className="slide3"></li>
            </ul>

            <button id="slider1_next" className="button_next" onClick={slide('next')}>&gt;</button>
            <button id="slider1_prev" className="button_prev" onClick={slide('prev')}>&lt;</button>

        </div>
    );
};

export default Slider;
