import React, { useState, useContext } from 'react';

import Context from './context';

import productImage from './images/menu/arrow-top.png';
import cross from './images/menu/arrow-top.png';

const CartAdd = function() {

    const pizzasCart = useContext(Context);

    const pizzas = pizzasCart.pizzasCart;

    console.log(pizzas);

    const [bin, setBin] = useState(function() {
        if (pizzas.length === 0) {
            return <div className="cart-empty-text">Корзина пуста</div>
        } else {
            return (
                <div className="cart-item-container">
                    <div className="item-image"><img src={productImage}></img></div>
                    <div className="item-title">Барбекю соус соус</div>
                    <div className="item-amount">
                        <div className="amount-changer"><span>-</span></div>
                        <div className="amount">1</div>
                        <div className="amount-changer"><span>+</span></div>
                    </div>
                    <div className="item-price"><span className="price-value">0.70 руб.</span></div>
                    <div className="item-delete"><div className="item-delete-btn"><img src={cross}></img></div></div>
                </div>
            )
        }
    });

    return bin;
};

export default CartAdd;
