import React from 'react';

const Slider = function () {

    const images = require('./json/images.json');

    const slidesArr = Object.values(images.slides);

    let marginLeft = 100;
    let slide1Clone;

    const nextSlide = function () {
        const slides = document.querySelectorAll('.slider-container ul li');
        const slide1 = slides[0];

        if(slide1Clone !== undefined && slide1Clone.style.marginLeft === 0) {
            marginLeft = 0;
            slides[0].setAttribute('style', `margin-left: -${marginLeft}%; transition: 0s all;`);
            slide1Clone = document.querySelector('.slide1clone');
            slide1Clone.remove();
            marginLeft = marginLeft + 100;
        }

        if(marginLeft === (slides.length) * 100) {
            slide1Clone = slide1.cloneNode();
            slide1Clone.className.add = 'slide1clone';
            slide1Clone.style.marginLeft = 0;
            const img = document.createElement('img');
            img.setAttribute('src', `${slidesArr[0]}`)
            slide1Clone.appendChild(img);
            const slider = document.querySelector('.slider-container ul');
            slider.appendChild(slide1Clone);
            slides[0].setAttribute('style', `margin-left: -${marginLeft}%; transition: .5s all;`)
        } else {
            slides[0].setAttribute('style', `margin-left: -${marginLeft}%; transition: .5s all;`)
            marginLeft = marginLeft + 100;
        }
    };

    // const prevNext = function () {
    //    if (document.querySelector('.margin-left') === null) {
    //         const slide1 = document.querySelector('.slide1');
    //         slide1.classList.add('margin-left');
    //    } else {
    //         const slide1 = document.querySelector('.slide1');
    //         slide1.classList.remove('margin-left');
    //    }
    // };

    // const prevSlide = function () {

    // };

    console.log(slidesArr)

    return (
        <div className="slider-container">
            <ul className="slider">
                {slidesArr.map((item, index) => {
                    return <li data-index={index} key={index}><img src={item}/></li>
                })}
            </ul>

            <button id="slider1_next" className="button_next" onClick={nextSlide}>&gt;</button>
            <button id="slider1_prev" className="button_prev">&lt;</button>

        </div>
    );
};

export default Slider;
