import React, { useContext, useState } from 'react';
import Context from './../context';

const PizzaProducts = function () {

    const PizzaProducts = require('./../json/products.json');

    const productArr = Object.values(PizzaProducts.pizzas);

    let {cart, setCart, changeCartBtn, setChangeCartBtn} = useContext(Context);

    const toCart = function (event) {
        const li = event.target.closest('li');

        let cartArr = {};

        if (cart.length > 0) {
            cartArr = JSON.parse(cart);
        }

        if (!cartArr.pizzas) cartArr.pizzas = [];

        cartArr.pizzas.push(li.dataset.id);

        let cartStr = JSON.stringify(cartArr);
        localStorage.setItem('cart', cartStr);

        const cartArray = Object.values(cartArr);

        let keys = Object.keys(cartArray);

        keys.forEach(key => {
            if(cartArray[key].indexOf(li.dataset.id) !== -1) {
                const button = event.target.closest('button');
                button.classList.add('at-cart');
                button.innerHTML = 'В корзине';
                button.disabled = true;
                setChangeCartBtn(1);
            }
            setChangeCartBtn(0)
        });

        setCart(cartStr);
    };

    return (
        <ul className="pizzas-goods">{
            productArr.map((item, index) =>
                <li className="product" key={index} data-id={index}>
                    <div className="product-img">
                        <img src={item.img}></img>
                    </div>
                    <div className="product-description">
                        <h3>{item.name}</h3>
                        <div className="size-weight">{item.size}</div>
                        <div className="description">{item.description}</div>
                    </div>
                    <div className="add-container">
                        <div className="cost">{item.cost}</div>
                        {changeCartBtn !== 1 ? <button className="add-btn" onClick={toCart} data-id={item.id}>В корзину</button> : <button className="at-cart" data-id={item.id}>В корзине</button>}
                    </div>
                </li>
            )    
        }</ul>
    );
};

export default PizzaProducts;
