import React, { useEffect } from 'react';
import Slider from "react-slick";

const SliderComponent = function () {

    const images = require('./json/images.json');

    const slidesArr = Object.values(images.slides);



    // async function autoSlide (){
    //     setInterval(() => {
    //         nextSlide();
    //     }, 5000)
    // };

    // autoSlide();

    // useEffect(function () {
	// 	setTimeout(() => {
    //         autoSlide();
    //     }, 5000)
	// }, autoSlide);

    return (
        <div className="slider-container">
            <ul className="slider">
                {slidesArr.map((item, index) => {
                    return <li data-index={index} key={index}><img src={item}/></li>
                })}
            </ul>

            <button id="slider1_next" className="button_next">&gt;</button>
            <button id="slider1_prev" className="button_prev">&lt;</button>

        </div>
    );
};

export default Slider;
